import { useState, useEffect, ChangeEvent } from 'react';

import { fetchData } from './utils/robots-api.utils';

import CardList from './components/card-list/card-list.component';
import SearchBox from './components/search-box/search-box.component';
// import './App.css';
import styled from 'styled-components';

export type Robot = {
  id: string;
  name: string;
  email: string;
};

const App = () => {
  const [searchField, setsearchField] = useState('');
  const [robots, setrobots] = useState<Robot[]>([]);
  const [filteredRobots, setfilteredRobots] = useState(robots);

  useEffect(() => {
    const getRobotUsers = async () => {
      const users = await fetchData<Robot[]>(
        'https://jsonplaceholder.typicode.com/users'
      );
      setrobots(users);
    };
    getRobotUsers();
  }, []);

  const onSearchChange = (event: ChangeEvent<HTMLInputElement>): void => {
    const searchFieldString = event.target.value.toLowerCase();
    setsearchField(searchFieldString);
  };
  useEffect(() => {
    const newfilteredRobots = robots.filter((monster) => {
      return monster.name.toLowerCase().includes(searchField);
    });
    setfilteredRobots(newfilteredRobots);
  }, [robots, searchField]);

  return (
    <div className="App">
      <h1 className="app-title">Robot Searcher</h1>
      <SearchBox
        className="robots-search-box"
        placeholder="search robots"
        onChangeHandler={onSearchChange}
      />
      <CardList robots={filteredRobots} />
    </div>
  );
};

// export default App;
export const StyledApp = styled(App)`
  @import url('https://fonts.googleapis.com/css2?family=Rubik+80s+Fade&display=swap');

  body {
    margin: 0;
    padding: 0;
    font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI',
      Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue',
      sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background: linear-gradient(
      rgba(198, 236, 249) 0%,
      rgba(255, 255, 255) 100%
    );
    background-repeat: no-repeat;
    text-align: center;
    color: rgb(71, 71, 71);
  }

  .app-title {
    margin: 50px;
    font-family: 'Rubik 80s Fade', cursive;
    font-size: 75px;
  }
`;
